const Header = (props) => {
    return (
      <h1>{props.course}</h1>
    )
}

const Content = (props) => {
    return (
      <>
        <p>
          {props.parts} {props.exercises1}
        </p>
        <p>
          {props.parts} {props.exercises2}
        </p>
        <p>
          {props.parts} {props.exercises}
        </p>
      </>
    )
}

const Total = (props) => {
  return (
      <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

export default App
