import { useState } from 'react'

const Button = ({ onClick, text }) => ( 
  <button onClick={onClick}>
    {text}
  </button>
)

const Statistics = ({ text, stat }) => (
  <div>
    <h1>Statistics</h1>
    <Statistic text={"good"} stat={good} />
    <Statistic text={"neutral"} stat={neutral} />
    <Statistic text={"bad"} stat={bad} />
    <Statistic text={"all"} stat={good + bad + neutral} />
    <Statistic text={"average"} stat={calculateAverage()} />
    <Statistic text={"% positive"} stat={good / (good + neutral + bad)} />
  </div>
)

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)  

  function calculateAverage() {
    return (good - bad) / (good + bad + neutral)
  }

  return (
    <div>
      <h1>Give Feedback</h1>
      <Button onClick={() => setGood(good + 1)} text={"good"} />
      <Button onClick={() => setNeutral(neutral + 1)} text={"neutral"} />
      <Button onClick={() => setBad(bad + 1)} text={"bad"} />
      <Statistics ></Statistics>
    </div>
  )
}

export default App
