import { useState } from 'react'

const Button = ({ onClick, text }) => ( 
  <button onClick={onClick}>
    {text}
  </button>
)

const Statistics = ({ good, neutral, bad }) => { 

  function calculateAverage() {
    return (good - bad) / (good + bad + neutral)
  }

  return (
  <div>
    <h1>Statistics</h1>
    <Statistic text={`good ${good}`}/>
    <Statistic text={`neutral ${neutral}`}/>
    <Statistic text={`bad ${bad}`} stat={bad} />
    <Statistic text={`all ${good + bad + neutral}`} />
    <Statistic text={`average ${calculateAverage()}`}/>
    <Statistic text={`positive ${good / (good + bad + neutral)}`}/>
  </div>
  )
}

const Statistic = ({ text }) => (
  <div>
    {text}
  </div>
)

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)  


  return (
    <div>
      <h1>Give Feedback</h1>
      <Button onClick={() => setGood(good + 1)} text={"good"} />
      <Button onClick={() => setNeutral(neutral + 1)} text={"neutral"} />
      <Button onClick={() => setBad(bad + 1)} text={"bad"} />
      <Statistics good={good} neutral={neutral} bad={bad}/>
    </div>
  )
}

export default App
